-- Nova Função RPC otimizada com metadados de exceção para UI V3
-- Nova Função RPC corrigida (resolvendo ambiguidade de colunas)
CREATE OR REPLACE FUNCTION public.verificar_elegibilidade(
    p_codigo_norma TEXT,
    p_artigo TEXT,
    p_paragrafo TEXT DEFAULT NULL,
    p_inciso TEXT DEFAULT NULL,
    p_alinea TEXT DEFAULT NULL
)
RETURNS TABLE (
    resultado VARCHAR,
    tipo_crime TEXT,
    observacoes TEXT,
    motivo TEXT,
    item_alinea_e VARCHAR,
    excecoes_detalhes TEXT
) 
LANGUAGE plpgsql
SECURITY DEFINER
AS $$
DECLARE
    v_record record;
    v_excecoes_list TEXT;
BEGIN
    -- 1. Buscar o registro mais específico (usando alias 't' para evitar conflito com parâmetros de retorno)
    -- REGRA: se usuário não especificou parágrafo, buscar o CAPUT (não qualquer parágrafo)
    -- Isso segue a tabela oficial onde "Art. X" sem especificação = caput
    SELECT 
        t.eh_excecao, t.tipo_crime, t.observacoes, t.item_alinea_e
    INTO v_record
    FROM public.crimes_inelegibilidade t
    WHERE t.codigo = p_codigo_norma 
      AND t.artigo = p_artigo
      AND (
          -- Se parágrafo especificado: buscar exatamente esse parágrafo
          (p_paragrafo IS NOT NULL AND t.paragrafo = p_paragrafo)
          OR
          -- Se parágrafo NÃO especificado: buscar o caput (paragrafo NULL ou 'caput')
          (p_paragrafo IS NULL AND (t.paragrafo IS NULL OR LOWER(t.paragrafo) = 'caput'))
      )
      AND (p_inciso IS NULL OR t.inciso = p_inciso)
      AND (p_alinea IS NULL OR t.alinea = p_alinea)
    ORDER BY 
      (t.paragrafo IS NOT DISTINCT FROM p_paragrafo) DESC,
      (t.inciso IS NOT DISTINCT FROM p_inciso) DESC,
      (t.alinea IS NOT DISTINCT FROM p_alinea) DESC,
      t.eh_excecao ASC 
    LIMIT 1;

    -- 2. Buscar TODAS as exceções para este artigo (Coluna central no UI V3)
    SELECT string_agg(
        CASE 
            WHEN t2.paragrafo IS NOT NULL THEN 'Parágrafo ' || t2.paragrafo 
            WHEN t2.inciso IS NOT NULL THEN 'Inciso ' || t2.inciso 
            WHEN t2.alinea IS NOT NULL THEN 'Alínea ' || t2.alinea 
            ELSE 'Artigo/Caput'
        END || COALESCE(' (' || t2.observacoes || ')', ''), 
        '; '
    ) INTO v_excecoes_list
    FROM public.crimes_inelegibilidade t2
    WHERE t2.codigo = p_codigo_norma 
      AND t2.artigo = p_artigo 
      AND t2.eh_excecao = TRUE;

    IF v_record IS NULL THEN
        RETURN QUERY SELECT 
            'NAO_CONSTA'::VARCHAR, 
            NULL::TEXT, 
            NULL::TEXT, 
            'Artigo não mapeado como impeditivo.'::TEXT,
            NULL::VARCHAR,
            NULL::TEXT;
        RETURN;
    END IF;

    -- 3. Retornar os dados estruturados para o Modal V3
    RETURN QUERY 
    SELECT 
      (CASE WHEN v_record.eh_excecao THEN 'ELEGIVEL' ELSE 'INELEGIVEL' END)::VARCHAR, 
      v_record.tipo_crime::TEXT, 
      v_record.observacoes::TEXT, 
      ('Artigo consta na tabela de inelegibilidade (Item ' || COALESCE(v_record.item_alinea_e, '?') || ')')::TEXT,
      v_record.item_alinea_e::VARCHAR,
      v_excecoes_list::TEXT;
END;
$$;

